element.calculation:
(function calculatedFieldValue(current) {

	// Add your code here
	var curr_total = current.curr_sunday_hrs +
		current.curr_monday_hrs + 
		current.curr_tuesday_hrs +
		current.curr_wednesday_hrs +
		current.curr_thursday_hrs +
		current.curr_friday_hrs + 
		current.curr_saturday_hrs;
	return curr_total;  // return the calculated value

})(current);
